import React, { useState } from 'react';
import Calendar from './Calendar';
import { useHistory } from 'react-router-dom';

const Schedule = () => {
  const [availability, setAvailability] = useState('');
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Store the availability data in local storage or pass it via navigation state
    localStorage.setItem('availability', availability);
    history.push('/profile');
  };

  return (
    <div className="container mt-4">
      <header className="bg-dark text-white text-center py-5">
        <h1>Set Schedule</h1>
      </header>
      <section id="schedule-section">
        <h2>Set your availability</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="availability">Select your available times:</label>
            <textarea
              id="availability"
              name="availability"
              rows="10"
              className="form-control"
              placeholder="Enter your available times here..."
              value={availability}
              onChange={(e) => setAvailability(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary">Continue</button>
        </form>
        <Calendar />
      </section>
    </div>
  );
};

export default Schedule;
